function initBasicExample() {
  const buttons = [document.querySelector('#openBasic'), document.querySelector('#closeBasic')];
  const drawer = document.querySelector('#defaultDrawer');

  buttons.forEach((button) => {
    button.addEventListener('click', () => {
      if (drawer.hasAttribute('open')) {
        drawer.removeAttribute('open');
      } else {
        drawer.setAttribute('open', true);
      }
    });
  });
}

function initCustomExample() {
  const buttons = [document.querySelector('#openCustom'), document.querySelector('#closeCustom')];
  const drawer = document.querySelector('#defaultDrawer');

  buttons.forEach((button) => {
    button.addEventListener('click', () => {
      if (drawer.hasAttribute('open')) {
        drawer.removeAttribute('open');
      } else {
        drawer.setAttribute('open', true);
      }
    });
  });
}

function initExamples(initCount) {
  initCount = initCount || 0;

  try {
    initBasicExample();
    initCustomExample();
  } catch (err) {
    if (initCount <= 20) {
      // setTimeout handles issue where content is sometimes loaded after the functions get called
      setTimeout(() => {
        initExamples(initCount + 1);
      }, 100);
    }
  }
}

export { initExamples };
