import { A as AuroDrawer, i as initBasicExample } from './auro-drawer.min.js';

function initCustomExample() {
  const buttons = [
    document.querySelector("#openCustom"),
    document.querySelector("#closeCustom"),
  ];
  const drawer = document.querySelector("#customDrawer");

  drawer.triggerElement = buttons[0];
  buttons[1].addEventListener("click", () => {
    drawer.removeAttribute("open");
  });
}

AuroDrawer.register();
AuroDrawer.register("custom-drawer");

function initExamples(initCount) {
  // biome-ignore lint/style/noParameterAssign: recursion counter
  initCount = initCount || 0;

  try {
    initBasicExample();
    initCustomExample();
  } catch (_err) {
    if (initCount <= 20) {
      // setTimeout handles issue where content is sometimes loaded after the functions get called
      setTimeout(() => {
        initExamples(initCount + 1);
      }, 100);
    }
  }
}

export { initExamples };
